mapper.select:
SELECT DISTINCT parking_district
FROM parking
WHERE parking_province = #{province}

mapper.update:
UPDATE car c
JOIN parking p
    ON p.parking_province = #{parking.parkingProvince}
    AND p.parking_district = #{parking.parkingDistrict}
    AND p.parking_name = #{parking.parkingName}
JOIN model m
    ON m.model_name = #{model.modelName}
    AND m.model_brand = #{model.modelBrand}
SET
c.car_year = #{carYear},
c.car_fuel = #{carFuel},
c.car_grade = #{carGrade},
c.model_id = m.model_id,
c.rental_station = p.parking_id
WHERE c.car_id = #{carId};

mapper.insert:
INSERT INTO car (car_year, car_fuel, car_grade, model_id, rental_station)
VALUES (
#{carYear},
#{carFuel},
#{carGrade},
(SELECT model_id FROM model WHERE model_name = #{model.modelName}),
(SELECT parking_id FROM parking
WHERE parking_province = #{parking.parkingProvince}
AND parking_district = #{parking.parkingDistrict}
AND parking_name = #{parking.parkingName})
);
