mapper.update:
UPDATE fast_reservation
SET car_id=#{carId}, rental_location=#{rentalLocation}, rental_datetime=#{rentalDatetime}, return_location=#{returnLocation},return_datetime= #{returnDatetime}
WHERE reservation_id=#{reservationId};

mapper.select:
SELECT m.model_name, m.model_category, m.model_seate_num, m.model_transmission, m.model_amount_day, m.model_amount_hour, c.car_year, c.car_fuel, c.car_grade, c.car_options, c.rental_station FROM car c JOIN model m ON c.model_id = m.model_id -- 차량 모델 정보 조회 JOIN parking p ON c.rental_station = p.parking_id -- 차량의 대여 지점(지역) 조회 WHERE p.parking_province = #{province} AND p.parking_district = #{district} -- 선택한 지역 필터링 AND NOT EXISTS ( -- 이미 해당 지역과 시간에 예약된 차량 목록 제외 SELECT 1 FROM fast_reservation fr WHERE fr.car_id = c.car_id AND ( (#{rentalDatetime} BETWEEN fr.rental_datetime AND fr.return_datetime) OR (#{returnDatetime} BETWEEN fr.rental_datetime AND fr.return_datetime) OR (fr.rental_datetime BETWEEN #{rentalDatetime} AND #{returnDatetime}) ) )
AND m.model_category = #{modelCategory}
AND m.model_name = NULLIF(#{modelName}, '')
