mapper.update:
UPDATE short_reservation
SET car_id=#{carId}, rental_station_start=#{rentalLocation}, reservation_s_start_date=#{rentalDatetime}, reservation_s_end_date= #{returnDatetime}
WHERE reservation_s_id=#{reservationId};

mapper.select:
SELECT m.model_id, m.model_name, m.model_brand, m.model_category, m.model_seate_num, m.model_transmission, m.model_amount_day, m.model_amount_hour, c.car_id, c.car_year, c.car_fuel, c.car_grade, c.car_options, c.rental_station FROM car c JOIN model m ON c.model_id = m.model_id -- 차량 모델 정보 조회 JOIN parking p ON c.rental_station = p.parking_id -- 차량의 대여 지점(지역) 조회 WHERE p.parking_province = #{province} AND p.parking_district = #{district} -- 선택한 지역 필터링 AND c.car_category = 1 -- 🚀 메인 쿼리에서도 필터링 AND NOT EXISTS ( -- 이미 해당 지역과 시간에 예약된 차량 목록 제외 SELECT 1 FROM short_reservation sr WHERE sr.car_id = c.car_id AND ( (#{rentalDatetime} BETWEEN sr.reservation_s_start_date AND sr.reservation_s_end_date) OR (#{returnDatetime} BETWEEN sr.reservation_s_start_date AND sr.reservation_s_end_date) OR (sr.reservation_s_start_date BETWEEN #{rentalDatetime} AND #{returnDatetime}) ) )
AND m.model_category = #{modelCategory}
AND m.model_name = #{modelName}

mapper.update:
UPDATE reservation
SET short_reservation_id = #{reservationId}
WHERE reservation_id = #{reservationId};

UPDATE car
SET car_status = 0
WHERE car_id = #{carId};
